mapper.select:
		select `id`,`type`,`url`,`create_date` from sys_file

		  		   and id = #{id} 
		  		   and type = #{type} 
		  		   and url = #{url} 
		  		   and create_date = #{createDate} 

				and dept_id in (select comun_id from (
				select t1.id,t1.comun_id,
				if(find_in_set(parent_id,@pids) >0 or id=@pids,@pids := concat(@pids,',', id),0) as ischild
				from (
				select comun_id, dept_id id,parent_id from sys_dept t where t.del_flag =1 order by parent_id, id
				) t1,
				(select@pids := #{deptId}) t2
				) t3 where ischild !=0)




                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from sys_file

  		   and id = #{id} 
  		   and type = #{type} 
  		   and url = #{url} 
  		   and create_date = #{createDate} 

		 and dept_id in (select comun_id from (
		 select t1.id,t1.comun_id,
		 if(find_in_set(parent_id,@pids) >0 or id=@pids,@pids := concat(@pids,',', id),0) as ischild
		 from (
		 select comun_id, dept_id id,parent_id from sys_dept t where t.del_flag =1 order by parent_id, id
		 ) t1,
		 (select@pids := #{deptId}) t2
		 ) t3 where ischild !=0)
