mapper.select:
		select a.id,a.year,a.organ_info_id,a.director_positon,a.director_name,a.political_landscape,a.gender,a.education_level,a.is_former,a.id_card,a.phone_number,a.mobile_phone,a.is_full_time,a.work_unit,a.unit_position,a.unit_address,a.other_social_duties,a.preparer,a.create_time,a.update_time,a.is_delete,b.company_name from organ_director_info a
		left join organ_info b on a.organ_info_id = b.id

			a.is_delete=0
		  		   and id = #{id} 
		  		   and year = #{year} 
		  		   and organ_info_id = #{organInfoId} 
		  		   and director_positon = #{directorPositon} 
		  		   and director_name = #{directorName} 
		  		   and political_landscape = #{politicalLandscape} 
		  		   and gender = #{gender} 
		  		   and education_level = #{educationLevel} 
		  		   and is_former = #{isFormer} 
		  		   and id_card = #{idCard} 
		  		   and phone_number = #{phoneNumber} 
		  		   and mobile_phone = #{mobilePhone} 
		  		   and is_full_time = #{isFullTime} 
		  		   and work_unit = #{workUnit} 
		  		   and unit_position = #{unitPosition} 
		  		   and unit_address = #{unitAddress} 
		  		   and other_social_duties = #{otherSocialDuties} 
		  		   and preparer = #{preparer} 
		  		   and create_time = #{createTime} 
		  		   and update_time = #{updateTime} 




                order by ${sort} ${order}


                order by a.id desc



			limit #{offset}, #{limit}

