mapper.select:
		select `id`,`company_name`,`company_code`,`manage_type`,`unit_type`,`business_unit`,`registration_date`,`certificate_end_time`,`sponsor_information`,`egistered_capital`,`source_capital`,`registered_address`,`contact_address`,`residential_occupancy`,`contact_name`,`contact_position`,`contact_phone`,`contact_email`,`contact_number`,`contact_fax`,`web_site_address`,`preparer`,`create_time`,`update_time`,`is_delete` from organ_info

		  		   and id = #{id} 
		  		   and company_name like '%${companyName}%' 
		  		   and company_code = #{companyCode} 
		  		   and manage_type = #{manageType} 
		  		   and unit_type = #{unitType} 
		  		   and business_unit = #{businessUnit} 
		  		   and registration_date = #{registrationDate} 
		  		   and certificate_end_time = #{certificateEndTime} 
		  		   and sponsor_information = #{sponsorInformation} 
		  		   and egistered_capital = #{egisteredCapital} 
		  		   and source_capital = #{sourceCapital} 
		  		   and registered_address = #{registeredAddress} 
		  		   and contact_address = #{contactAddress} 
		  		   and residential_occupancy = #{residentialOccupancy} 
		  		   and contact_name = #{contactName} 
		  		   and contact_position = #{contactPosition} 
		  		   and contact_phone = #{contactPhone} 
		  		   and contact_email = #{contactEmail} 
		  		   and contact_number = #{contactNumber} 
		  		   and contact_fax = #{contactFax} 
		  		   and web_site_address = #{webSiteAddress} 
		  		   and preparer = #{preparer} 
		  		   and create_time = #{createTime} 
		  		   and update_time = #{updateTime} 
		  		   and is_delete = 0



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

