mapper.select:
		select a.id,a.year,a.organ_info_id,a.unit_member_number,a.individual_member_number,a.start_date,a.term_time,a.director_number,a.standing_director_number,a.supervisor_number,a.secretariat_number,a.branches_number,a.representative_number,a.mechanisms_number,a.preparer,a.create_time,a.update_time ,b.company_name from organ_member_staff a
         left join organ_info b on a.organ_info_id = b.id


					a.is_delete =  0
		  		   and a.id = #{id} 
		  		   and a.year = #{year} 
		  		   and a.organ_info_id = #{organInfoId} 
		  		   and a.unit_member_number = #{unitMemberNumber} 
		  		   and a.individual_member_number = #{individualMemberNumber} 
		  		   and a.start_date = #{startDate} 
		  		   and a.term_time = #{termTime} 
		  		   and a.director_number = #{directorNumber} 
		  		   and a.standing_director_number = #{standingDirectorNumber} 
		  		   and a.supervisor_number = #{supervisorNumber} 
		  		   and a.secretariat_number = #{secretariatNumber} 
		  		   and a.branches_number = #{branchesNumber} 
		  		   and a.representative_number = #{representativeNumber} 
		  		   and a.mechanisms_number = #{mechanismsNumber} 
		  		   and a.preparer = #{preparer} 
		  		   and a.create_time = #{createTime} 
		  		   and a.update_time = #{updateTime} 



                order by ${sort} ${order}


                order by a.id desc



			limit #{offset}, #{limit}

