mapper.select:
		select `id`,`title`,`author`,`content`,`public_time`,`tags`,`news_type`,`status`,`allow_comment`,`allow_evaluation_star`,`update_time` from organ_news a
         a.is_delete=0
		  		   and id = #{id} 
		  		   and title = #{title} 
		  		   and author = #{author} 
		  		   and content = #{content} 
		  		   and public_time = #{publicTime} 
		  		   and tags = #{tags} 
		  		   and news_type = #{newsType} 
		  		   and status = #{status} 
		  		   and allow_comment = #{allowComment} 
		  		   and allow_evaluation_star = #{allowEvaluationStar} 
		  		   and update_time = #{updateTime} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select a.id,a.title,a.author,a.content,a.public_time,a.tags,a.news_type,a.status,a.allow_comment,a.allow_evaluation_star,a.update_time,b.company_name from organ_news a
left join organ_info b on a.organ_info_id = b.id
where news_type in

		#{type}

	and is_delete = 0


		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

