mapper.select:
		select a.id,a.year,a.organ_info_id,a.party_members_number,a.full_time_party_members_number,a.organization_number,a.this_year_add_number,a.party_type,a.party_relationship,a.party_building_liaison,a.league_organization_number,a.trade_union_number,a.preparer,a.create_time,a.update_time,a.is_delete,b.company_name from organ_party_info a
		left join organ_info b on a.organ_info_id = b.id
		 a.is_delete=0
		  		   and id = #{id} 
		  		   and year = #{year} 
		  		   and organ_info_id = #{organInfoId} 
		  		   and party_members_number = #{partyMembersNumber} 
		  		   and full_time_party_members_number = #{fullTimePartyMembersNumber} 
		  		   and organization_number = #{organizationNumber} 
		  		   and this_year_add_number = #{thisYearAddNumber} 
		  		   and party_type = #{partyType} 
		  		   and party_relationship = #{partyRelationship} 
		  		   and party_building_liaison = #{partyBuildingLiaison} 
		  		   and league_organization_number = #{leagueOrganizationNumber} 
		  		   and trade_union_number = #{tradeUnionNumber} 
		  		   and preparer = #{preparer} 
		  		   and create_time = #{createTime} 
		  		   and update_time = #{updateTime} 
		  		   and is_delete = #{isDelete} 



                order by ${sort} ${order}


                order by b.id desc



			limit #{offset}, #{limit}

select count(*) from organ_party_info
  is_delete=0
  		   and id = #{id} 
